import React, { Component } from 'react'
import { Button, ButtonGroup, ButtonToolbar } from 'react-bootstrap-buttons';


class Fave extends Component {
    constructor(props) {
        super(props);
        this.state = {
            // isFave: false,
        };
    }

    handleClick = (e) => {
        console.log("handling Fave click!");
        e.stopPropagation();
        this.props.onFaveToggle()
    };

    render() {
        const isFave = (this.props.isFave) ? <Button variant="danger">Unlike</Button> : <Button variant="danger">Like</Button>
        console.log("isFave", isFave, this.props.isFave)
        return (
            <div className={"film-row-fave " + isFave} onClick={this.handleClick}>
                <p className="material-icons">{(this.props.isFave) ? <Button variant="danger">Unlike</Button> : <Button variant="danger">Like</Button>}</p>
            </div>
        );
    }
}

export default Fave;
