import React, { Component } from 'react'
import FilmRow from './FilmRow';
// import FilmPoster from './FilmPoster';
// import FilmPoster from './FilmPoster';

class FilmListing extends Component {
    constructor(props) {
        super(props);
        this.state = {
            filter: 'all',
        };
    }
    handleFilterClick = (filter) => {
        console.log('Setting filter to ' + filter);
        this.setState({
            filter: filter,
        });
    }

    render() {
        // console.log(this.props.faves.includes(this.props.films[0]), this.props.films)
        const allFilms = this.props.films.map((film, index) => {
            return (
                <FilmRow isFave={(this.props.faves.includes(film) <= 0) ? false : true} onFaveToggle={() => this.props.onFaveToggle(film)} title={film.title} poster_path={film.poster_path} key={index} year={(new Date(film.release_date)).getFullYear()} />

            )
        })
        const allFaves = this.props.faves.map((film, index) => {
            return (
                <FilmRow isFave={(this.props.faves.includes(film) <= 0) ? false : true} onFaveToggle={() => this.props.onFaveToggle(film)} title={film.title} poster_path={film.poster_path} key={index} year={(new Date(film.release_date)).getFullYear()} />
            )
        })

        return (
            <div>
                <h1>FILMS</h1>

                    <div className={"filmAll" + (this.state.filter === "all" ? "is-active" : "")} onClick={() => this.handleFilterClick('all')}>
                        ALL
            <span className="allLength"> {this.props.films.length} </span>
                    </div>
                    <div className={"filmFav " + (this.state.filter === "faves" ? "is-active" : "")} onClick={() => this.handleFilterClick('faves')}>
                        FAVES
            <span className="favLength">{this.props.faves.length}</span>
                    </div>

                {(this.state.filter === 'all') ? allFilms : allFaves}
            </div>

        );
    }
}

export default FilmListing;
